import React, { useState } from "react";

const Home = () => {
  const [currentView, setCurrentView] = useState("welcome");
  const [uploadedImage, setUploadedImage] = useState(null);
  const [classificationResult, setClassificationResult] = useState("");
  const [disposalInstructions, setDisposalInstructions] = useState([]);

  // SVG Icons
  const CameraIcon = () => (
    <svg width="48" height="48" fill="none" stroke="currentColor" viewBox="0 0 24 24" className="text-white">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 9a2 2 0 012-2h.93a2 2 0 001.664-.89l.812-1.22A2 2 0 0110.07 4h3.86a2 2 0 011.664.89l.812 1.22A2 2 0 0018.07 7H19a2 2 0 012 2v9a2 2 0 01-2 2H5a2 2 0 01-2-2V9z" />
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 13a3 3 0 11-6 0 3 3 0 016 0z" />
    </svg>
  );

  const UploadIcon = () => (
    <svg width="32" height="32" fill="none" stroke="currentColor" viewBox="0 0 24 24" className="text-emerald-600">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" />
    </svg>
  );

  const CheckIcon = ({ size = 24 }) => (
    <svg width={size} height={size} fill="none" stroke="currentColor" viewBox="0 0 24 24" className="text-white">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M5 13l4 4L19 7" />
    </svg>
  );

  // ✅ Fallback instructions (same as your original)
  const getFallbackInstructions = (wasteType) => {
    const instructions = {
      cardboard: [
        "Remove any tape, labels, or packing materials.",
        "Flatten cardboard boxes to save space.",
        "Place clean cardboard in the recycling bin.",
        "Soiled or wet cardboard goes to general waste.",
      ],
      glass: [
        "Remove lids or caps from glass containers.",
        "Rinse the glass to remove residue.",
        "Place clean glass in the recycling bin.",
        "Wrap broken glass safely before disposal.",
      ],
      metal: [
        "Rinse cans to remove residue.",
        "Remove paper labels if possible.",
        "Place in the metal recycling bin.",
        "Aluminum and steel are recyclable.",
      ],
      paper: [
        "Remove plastic wrapping or tape.",
        "Ensure paper is clean and dry.",
        "Place in the paper recycling bin.",
        "Heavily soiled paper goes to general waste.",
      ],
      plastic: [
        "Rinse the plastic container to remove any food residue.",
        "Check for the recycling symbol (1, 2, or 5). If present, it’s recyclable.",
        "Place clean, recyclable plastics in the recycling bin.",
        "If not recyclable, dispose in general waste.",
      ],
      trash: [
        "Place non-recyclable waste in the general waste bin.",
        "Avoid mixing recyclables with trash.",
        "Seal trash bags to prevent spills.",
        "Dispose of hazardous waste separately.",
      ],
    };
    return instructions[wasteType] || instructions["plastic"];
  };

  const classifyImage = async (file) => {
    if (!file) return;
    setCurrentView("processing");
  
    try {
      const formData = new FormData();
      formData.append("image", file);
  
      const response = await fetch("http://localhost:5000/api/public/classify", {
        method: "POST",
        body: formData,
      });
  
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Server error');
      }
  
      const result = await response.json();
      
      setClassificationResult(result.category);
      
      // ✅ CONVERT TO ARRAY - THIS IS THE FIX
      let instructionsArray = [];
      if (result.instructions) {
        if (Array.isArray(result.instructions)) {
          instructionsArray = result.instructions;
        } else if (typeof result.instructions === 'string') {
          instructionsArray = result.instructions
            .split('\n')
            .map(s => s.trim())
            .filter(s => s.length > 0);
        }
      }
      
      setDisposalInstructions(instructionsArray);
      setCurrentView("classification");
  
    } catch (error) {
      console.error("Error:", error);
      alert("Failed to classify image. Check your server.");
      resetToWelcome();
    }
  };

  const resetToWelcome = () => {
    setUploadedImage(null);
    setClassificationResult("");
    setCurrentView("welcome");
  };

  // ✅ File input handler (UNCOMMENTED)
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setUploadedImage(e.target.result);
        classifyImage(file); // call classification
      };
      reader.readAsDataURL(file);
    }
  };

  // ✅ Camera capture handler (UNCOMMENTED)
  const handleCameraCapture = () => {
    const input = document.createElement("input");
    input.type = "file";
    input.accept = "image/*";
    input.capture = "camera";
    input.onchange = handleImageUpload;
    input.click();
  };

  // Screens
  if (currentView === "welcome") {
    return (
      <div className="flex flex-col bg-gradient-to-br from-lime-50 to-lime-100">
        <div className="flex flex-1 flex-col items-center justify-start gap-1 px-6 text-center py-5">
          <h2 className="text-5xl font-bold text-gray-800 mb-8">Time to Recycle!</h2>
          <p className="text-xl text-gray-500 mb-12 max-w-lg leading-relaxed">
            Help us identify your waste. Simply tap the button below to snap a photo or upload one from your gallery.
          </p>

          <div className="flex flex-col items-center gap-1">
            <button
              onClick={handleCameraCapture} // ✅ Now defined
              className="bg-white rounded-full p-6 shadow-2xl hover:scale-105 transition"
            >
              <div className="flex flex-col items-center gap-3">
                <div className="p-5 bg-lime-400 rounded-xl">
                  <CameraIcon />
                </div>
                <span className="text-xl font-semibold text-gray-800">
                  Capture Photo
                </span>
              </div>
            </button>

            <div className="flex items-center gap-4 w-52">
              <div className="flex-1 h-px bg-gray-300" />
              <span className="text-gray-500 font-medium">or</span>
              <div className="flex-1 h-px bg-gray-300" />
            </div>

            <div className="relative w-72">
              <input
                type="file"
                accept="image/*"
                onChange={handleImageUpload} // ✅ Now defined
                className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
              />
              <button className="bg-white border-2 border-lime-200 rounded-xl p-5 w-full shadow-lg">
                <div className="flex flex-col items-center gap-3">
                  <div className="p-4 bg-lime-50 rounded-lg">
                    <UploadIcon />
                  </div>
                  <span className="text-lg font-semibold text-gray-800">
                    Upload from Gallery
                  </span>
                </div>
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (currentView === "processing") {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-lime-50 to-lime-100 p-6">
        {uploadedImage && <img src={uploadedImage} alt="Uploaded" className="w-48 h-48 object-cover rounded-xl shadow-xl mb-6" />}
        <div className="w-16 h-16 border-4 border-lime-400 border-t-transparent rounded-full animate-spin mb-4"></div>
        <h2 className="text-2xl font-bold text-gray-800 mb-2">Analyzing Image...</h2>
        <p className="text-gray-600 text-center max-w-xs">
          Our AI is identifying your waste type to provide the best recycling guidance.
        </p>
      </div>
    );
  }

  if (currentView === "classification") {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-lime-50 to-lime-100 p-6">
        {uploadedImage && <img src={uploadedImage} alt="Classified" className="w-32 h-32 object-cover rounded-xl shadow-xl mb-6" />}
        <div className="mb-6">
          <div className="w-24 h-24 bg-white rounded-full flex items-center justify-center shadow-xl">
            <div className="w-12 h-12 bg-lime-400 rounded-full flex items-center justify-center">
              <CheckIcon size={24} />
            </div>
          </div>
        </div>
        <h2 className="text-3xl font-bold text-gray-800 mb-4">{classificationResult}</h2>
        <p className="text-gray-600 text-center mb-6">
          Great! We've identified your waste as {classificationResult.toLowerCase()}.
          Let's learn how to dispose of it properly.
        </p>
        <div className="flex flex-col gap-4 w-full max-w-xs">
          <button
            onClick={() => setCurrentView("instructions")}
            className="w-full bg-lime-400 text-white py-3 rounded-xl font-semibold hover:bg-lime-500 transition"
          >
            View Disposal Instructions
          </button>
          <button
            onClick={() => {
              setCurrentView("welcome");
              setUploadedImage(null);
              setClassificationResult("");
            }}
            className="w-full bg-white border border-gray-300 text-gray-700 py-3 rounded-xl font-semibold hover:bg-gray-100 transition"
          >
            Scan Another Item
          </button>
        </div>
      </div>
    );
  }

  if (currentView === "instructions") {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-lime-50 to-lime-100 p-6">
        <div className="bg-white rounded-2xl shadow-xl p-6 w-full max-w-md">
          <h2 className="text-xl font-bold text-gray-800 text-center mb-6">
            How to Dispose of {classificationResult}
          </h2>
          <div className="flex flex-col gap-4 mb-6">
            {disposalInstructions.map((instr, idx) => (
              <div key={idx} className="flex items-start gap-3">
                <div className={`w-6 h-6 flex items-center justify-center rounded-full text-white font-bold ${idx < 3 ? 'bg-lime-400' : 'bg-gray-400'}`}>
                  {idx + 1}
                </div>
                <p className="text-gray-700">{instr}</p>
              </div>
            ))}
          </div>
          <div className="flex flex-col gap-3">
            <button
              onClick={() => setCurrentView("confirmation")}
              className="w-full bg-lime-400 text-white py-3 rounded-xl font-semibold hover:bg-lime-500 transition"
            >
              Confirm Disposal
            </button>
            <button
              onClick={() => setCurrentView("welcome")}
              className="w-full bg-white border border-gray-300 text-gray-700 py-3 rounded-xl font-semibold hover:bg-gray-100 transition"
            >
              Scan Another Item
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (currentView === "confirmation") {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-lime-50 to-lime-100 p-6">
        <div className="bg-white rounded-2xl shadow-xl p-8 flex flex-col items-center gap-6">
          <h2 className="text-3xl font-bold text-gray-800">Thank You!</h2>
          <p className="text-gray-600 text-center">
            You've successfully disposed of {classificationResult.toLowerCase()} responsibly. Keep recycling!
          </p>
          <button
            onClick={() => {
              setCurrentView("welcome");
              setUploadedImage(null);
              setClassificationResult("");
            }}
            className="bg-lime-400 text-white py-3 px-6 rounded-xl font-semibold hover:bg-lime-500 transition"
          >
            Scan Another Item
          </button>
        </div>
      </div>
    );
  }

  return null;
};

export default Home;
