import React from 'react';

const Banner = () => {
  return (
    <div className="bg-white py-12 px-4">
      <div className="max-w-6xl mx-auto">
        {/* Main Banner Content */}
        <div className="flex flex-col lg:flex-row items-center justify-center gap-8 lg:gap-12">
          
          {/* Left Text - "Bin" */}
          <div className="text-center lg:text-right">
            <h1 className="text-6xl lg:text-8xl font-bold text-black font-handwriting">
              Bin
            </h1>
          </div>

          {/* Center Robot Placeholder */}
          <div className="flex flex-col items-center gap-4">
            {/* Robot Placeholder - Replace with actual 3D robot */}
            <div className="w-32 h-40 bg-lime-400 rounded-2xl flex items-center justify-center shadow-lg">
              <div className="text-black font-bold text-sm text-center">
                3D Robot<br/>Placeholder
              </div>
            </div>
            
            {/* Call-to-Action Button */}
            <button className="bg-lime-400 text-black px-8 py-3 rounded-xl font-semibold text-lg hover:bg-lime-500 transition-colors duration-200 shadow-lg">
              Scan now
            </button>
          </div>

          {/* Right Text - "sight" */}
          <div className="text-center lg:text-left">
            <h1 className="text-6xl lg:text-8xl font-bold text-black font-handwriting">
              sight
            </h1>
          </div>
        </div>

        {/* Floating Items - Blank Boxes */}
        <div className="relative mt-8 lg:mt-12">
          {/* Top Left - Cardboard Box */}
          <div className="absolute top-0 left-4 lg:left-8 w-16 h-16 bg-amber-200 border-2 border-amber-300 rounded-lg flex items-center justify-center">
            <span className="text-xs text-amber-700 font-medium">Box</span>
          </div>

          {/* Mid-Left - Champagne Bottle */}
          <div className="absolute top-8 left-0 lg:left-4 w-12 h-16 bg-green-200 border-2 border-green-300 rounded-lg flex items-center justify-center">
            <span className="text-xs text-green-700 font-medium">Bottle</span>
          </div>

          {/* Bottom Left - Salad Bowl */}
          <div className="absolute top-16 left-8 lg:left-12 w-14 h-14 bg-white border-2 border-gray-300 rounded-full flex items-center justify-center">
            <span className="text-xs text-gray-600 font-medium">Bowl</span>
          </div>

          {/* Mid-Bottom Left - Tin Can */}
          <div className="absolute top-20 left-2 w-10 h-12 bg-red-200 border-2 border-red-300 rounded-lg flex items-center justify-center">
            <span className="text-xs text-red-700 font-medium">Can</span>
          </div>

          {/* Top Right - Toilet Paper */}
          <div className="absolute top-0 right-4 lg:right-8 w-12 h-16 bg-white border-2 border-gray-300 rounded-lg flex items-center justify-center">
            <span className="text-xs text-gray-600 font-medium">Paper</span>
          </div>

          {/* Mid-Right - Bubble Tea */}
          <div className="absolute top-8 right-0 lg:right-4 w-12 h-16 bg-amber-200 border-2 border-amber-300 rounded-lg flex items-center justify-center">
            <span className="text-xs text-amber-700 font-medium">Cup</span>
          </div>

          {/* Bottom Right - Newspaper */}
          <div className="absolute top-16 right-8 lg:right-12 w-14 h-12 bg-gray-200 border-2 border-gray-300 rounded-lg flex items-center justify-center">
            <span className="text-xs text-gray-600 font-medium">News</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Banner;
