import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  // Map nav links to their routes
  const navLinks = [
    { name: 'Home', path: '/' },
    { name: 'About', path: '/about' },
    { name: 'Chat', path: '/chat' }
  ];

  // Get active link based on current path
  const getActiveLink = () => {
    const currentLink = navLinks.find(link => link.path === location.pathname);
    return currentLink ? currentLink.name : 'Home';
  };

  const activeLink = getActiveLink();

  // Handle navigation
  const handleNavClick = (link) => {
    navigate(link.path);
  };

  const handleHelpClick = () => {
    navigate('/help');
  };

  return (
    <nav className="bg-white shadow-sm">
      <div className="mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Brand Name */}
          <div className="flex-shrink-0 cursor-pointer" onClick={() => navigate('/')}>
            <h1 className="text-4xl font-bold text-black">
              Binsight
            </h1>
          </div>

          {/* Desktop Navigation Links */}
          <div className="hidden md:flex items-center">
            <div className="bg-lime-400 rounded-full px-8 py-3 flex space-x-8">
              {navLinks.map((link) => (
                <button
                  key={link.name}
                  onClick={() => handleNavClick(link)}
                  className={`text-black font-medium text-sm transition-colors duration-200 relative ${
                    activeLink === link.name
                      ? 'after:content-[""] after:absolute after:bottom-[-4px] after:left-0 after:right-0 after:h-0.5 after:bg-black'
                      : 'hover:text-gray-700'
                  }`}
                >
                  {link.name}
                </button>
              ))}
            </div>
          </div>

          {/* Help & Support Button */}
          <div className="hidden md:block">
            <button 
              onClick={handleHelpClick}
              className="bg-black text-white px-6 py-3 rounded-full font-medium text-sm hover:bg-gray-800 transition-colors duration-200"
            >
              Help&Support
            </button>
          </div>

          {/* Mobile menu button */}
          <div className="md:hidden">
            <button
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              className="text-black hover:text-gray-700 focus:outline-none focus:text-gray-700"
            >
              <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
              </svg>
            </button>
          </div>
        </div>

        {/* Mobile Navigation Menu */}
        {isMobileMenuOpen && (
          <div className="md:hidden">
            <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3 bg-lime-400 rounded-lg mt-2">
              {navLinks.map((link) => (
                <button
                  key={link.name}
                  onClick={() => {
                    handleNavClick(link);
                    setIsMobileMenuOpen(false);
                  }}
                  className={`block w-full text-left px-3 py-2 text-black font-medium text-sm rounded-md transition-colors duration-200 ${
                    activeLink === link.name
                      ? 'bg-black bg-opacity-10'
                      : 'hover:bg-black hover:bg-opacity-10'
                  }`}
                >
                  {link.name}
                </button>
              ))}
              <div className="pt-2">
                <button 
                  onClick={() => {
                    handleHelpClick();
                    setIsMobileMenuOpen(false);
                  }}
                  className="w-full bg-black text-white px-3 py-2 rounded-md font-medium text-sm hover:bg-gray-800 transition-colors duration-200"
                >
                  Help&Support
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
