import React, { useState } from 'react';

const Home = () => {
  const [currentView, setCurrentView] = useState('welcome');
  const [uploadedImage, setUploadedImage] = useState(null);
  const [classificationResult, setClassificationResult] = useState('');

  // SVG Icons as components
  const CameraIcon = () => (
    <svg width="48" height="48" fill="none" stroke="currentColor" viewBox="0 0 24 24" className="text-white">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 9a2 2 0 012-2h.93a2 2 0 001.664-.89l.812-1.22A2 2 0 0110.07 4h3.86a2 2 0 011.664.89l.812 1.22A2 2 0 0018.07 7H19a2 2 0 012 2v9a2 2 0 01-2 2H5a2 2 0 01-2-2V9z" />
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 13a3 3 0 11-6 0 3 3 0 016 0z" />
    </svg>
  );

  const UploadIcon = () => (
    <svg width="32" height="32" fill="none" stroke="currentColor" viewBox="0 0 24 24" className="text-emerald-600">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" />
    </svg>
  );

  const CheckIcon = ({ size = 24 }) => (
    <svg width={size} height={size} fill="none" stroke="currentColor" viewBox="0 0 24 24" className="text-white">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M5 13l4 4L19 7" />
    </svg>
  );

  // Mock classification function
  const classifyImage = (file) => {
    console.log('Classifying image:', file.name);
    setTimeout(() => {
      const wasteTypes = ['Plastic', 'Glass', 'Paper', 'Metal', 'Organic'];
      const randomType = wasteTypes[Math.floor(Math.random() * wasteTypes.length)];
      setClassificationResult(randomType);
      setCurrentView('classification');
    }, 1500);
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      console.log('File selected:', file.name);
      const reader = new FileReader();
      reader.onload = (e) => {
        setUploadedImage(e.target.result);
        setCurrentView('processing');
        classifyImage(file);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleCameraCapture = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = 'image/*';
    input.capture = 'camera';
    input.onchange = handleImageUpload;
    input.click();
  };

  const getDisposalInstructions = (wasteType) => {
    const instructions = {
      'Plastic': [
        'Rinse the plastic container to remove any food residue.',
        'Check for the recycling symbol (usually numbers 1, 2, or 5). If present, it\'s recyclable.',
        'Place clean, recyclable plastics in the designated recycling bin.',
        'If not recyclable, please dispose of it in the general waste bin.'
      ],
      'Glass': [
        'Remove any lids or caps from glass containers.',
        'Rinse the glass to remove food residue.',
        'Place clean glass in the glass recycling bin.',
        'Broken glass should be wrapped safely and put in general waste.'
      ],
      'Paper': [
        'Remove any plastic wrapping or tape from paper.',
        'Ensure paper is clean and dry.',
        'Place in the paper recycling bin.',
        'Heavily soiled paper should go in general waste.'
      ],
      'Metal': [
        'Rinse cans and containers to remove food residue.',
        'Remove any paper labels if possible.',
        'Place in the metal recycling bin.',
        'Aluminum and steel are both recyclable.'
      ],
      'Organic': [
        'Place food scraps in the organic waste bin.',
        'Remove any packaging materials first.',
        'Compost at home if possible.',
        'Avoid putting meat or dairy in home compost.'
      ]
    };
    
    return instructions[wasteType] || instructions['Plastic'];
  };

  if (currentView === 'welcome') {
    return (
      <div style={{ minHeight: '100vh', background: 'linear-gradient(to bottom right, #ecfdf5, #dcfce7)', display: 'flex', flexDirection: 'column' }}>
        {/* Header */}
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'flex-start', padding: '24px' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
            {/* <div style={{ width: '32px', height: '32px', backgroundColor: '#10b981', borderRadius: '8px', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
              <div style={{ width: '16px', height: '16px', backgroundColor: 'white', borderRadius: '2px' }}></div>
            </div> */}
            <img src="../assets/logo.png" alt='img'></img>
            <h1 style={{ fontSize: '24px', fontWeight: 'bold', color: '#1f2937', margin: 0 }}>BinSight</h1>
          </div>
        </div>

        {/* Main Content */}
        <div style={{ flex: 1, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', padding: '0 24px', textAlign: 'center' }}>
          <h2 style={{ fontSize: '36px', fontWeight: 'bold', color: '#1f2937', marginBottom: '24px' }}>Time to Recycle!</h2>
          <p style={{ color: '#6b7280', fontSize: '18px', marginBottom: '48px', maxWidth: '400px', lineHeight: '1.6' }}>
            Help us identify your waste. Simply tap the button below to snap a photo or upload one from your gallery.
          </p>

          {/* Upload Options */}
          <div style={{ display: 'flex', flexDirection: 'column', gap: '24px', alignItems: 'center' }}>
            {/* Camera Button */}
            <div style={{ position: 'relative' }}>
              <button
                onClick={handleCameraCapture}
                style={{
                  position: 'relative',
                  backgroundColor: 'white',
                  borderRadius: '50%',
                  padding: '48px',
                  border: 'none',
                  boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease'
                }}
                onMouseEnter={(e) => {
                  e.target.style.transform = 'scale(1.05)';
                  e.target.style.boxShadow = '0 35px 60px -12px rgba(0, 0, 0, 0.35)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.transform = 'scale(1)';
                  e.target.style.boxShadow = '0 25px 50px -12px rgba(0, 0, 0, 0.25)';
                }}
              >
                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '12px' }}>
                  <div style={{ padding: '24px', backgroundColor: '#10b981', borderRadius: '16px' }}>
                    <CameraIcon />
                  </div>
                  <span style={{ fontSize: '20px', fontWeight: '600', color: '#1f2937' }}>
                    Capture Photo
                  </span>
                </div>
              </button>
            </div>

            {/* Or Divider */}
            <div style={{ display: 'flex', alignItems: 'center', gap: '16px', width: '200px' }}>
              <div style={{ flex: 1, height: '1px', backgroundColor: '#d1d5db' }}></div>
              <span style={{ color: '#6b7280', fontWeight: '500' }}>or</span>
              <div style={{ flex: 1, height: '1px', backgroundColor: '#d1d5db' }}></div>
            </div>

            {/* Upload Button */}
            <div style={{ position: 'relative', width: '300px' }}>
              <input
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
                style={{ 
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '100%',
                  height: '100%',
                  opacity: 0,
                  cursor: 'pointer',
                  zIndex: 10
                }}
              />
              <button style={{
                position: 'relative',
                backgroundColor: 'white',
                borderRadius: '16px',
                padding: '32px',
                border: '2px dashed #a7f3d0',
                cursor: 'pointer',
                width: '100%',
                boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.1)',
                transition: 'all 0.3s ease'
              }}>
                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '12px' }}>
                  <div style={{ padding: '16px', backgroundColor: '#ecfdf5', borderRadius: '12px' }}>
                    <UploadIcon />
                  </div>
                  <span style={{ fontSize: '18px', fontWeight: '600', color: '#1f2937' }}>
                    Upload from Gallery
                  </span>
                </div>
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (currentView === 'processing') {
    return (
      <div style={{ minHeight: '100vh', background: 'linear-gradient(to bottom right, #ecfdf5, #dcfce7)', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '24px' }}>
        <div style={{ textAlign: 'center' }}>
          {uploadedImage && (
            <div style={{ marginBottom: '32px' }}>
              <img
                src={uploadedImage}
                alt="Uploaded waste"
                style={{ width: '192px', height: '192px', objectFit: 'cover', borderRadius: '16px', boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)', margin: '0 auto', display: 'block' }}
              />
            </div>
          )}

          <div style={{ marginBottom: '24px' }}>
            <div style={{ 
              width: '64px', 
              height: '64px', 
              backgroundColor: '#10b981', 
              borderRadius: '50%', 
              margin: '0 auto',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}>
              <div style={{
                width: '32px',
                height: '32px',
                border: '4px solid white',
                borderTop: '4px solid transparent',
                borderRadius: '50%',
                animation: 'spin 1s linear infinite'
              }}></div>
            </div>
          </div>

          <h2 style={{ fontSize: '30px', fontWeight: 'bold', color: '#1f2937', marginBottom: '16px' }}>Analyzing Image...</h2>
          <p style={{ color: '#6b7280', fontSize: '18px' }}>
            Our AI is identifying your waste type to provide the best recycling guidance.
          </p>
        </div>
        
        <style>{`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}</style>
      </div>
    );
  }

  if (currentView === 'classification') {
    return (
      <div style={{ minHeight: '100vh', background: 'linear-gradient(to bottom right, #ecfdf5, #dcfce7)', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '24px' }}>
        <div style={{ textAlign: 'center', maxWidth: '400px' }}>
          {uploadedImage && (
            <div style={{ marginBottom: '32px' }}>
              <img
                src={uploadedImage}
                alt="Classified waste"
                style={{ width: '128px', height: '128px', objectFit: 'cover', borderRadius: '16px', boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.1)', margin: '0 auto', display: 'block' }}
              />
            </div>
          )}

          <div style={{ marginBottom: '24px' }}>
            <div style={{ width: '96px', height: '96px', backgroundColor: 'white', borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center', boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)', margin: '0 auto' }}>
              <div style={{ width: '48px', height: '48px', backgroundColor: '#10b981', borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                <CheckIcon size={24} />
              </div>
            </div>
          </div>

          <h2 style={{ fontSize: '36px', fontWeight: 'bold', color: '#1f2937', marginBottom: '24px' }}>{classificationResult}</h2>
          <p style={{ color: '#6b7280', fontSize: '18px', marginBottom: '48px', lineHeight: '1.6' }}>
            Great! We've identified your waste as {classificationResult.toLowerCase()}. 
            Let's learn how to dispose of it properly.
          </p>

          <div style={{ display: 'flex', flexDirection: 'column', gap: '16px' }}>
            <button
              onClick={() => setCurrentView('instructions')}
              style={{
                width: '100%',
                backgroundColor: '#10b981',
                color: 'white',
                padding: '16px 32px',
                borderRadius: '16px',
                fontWeight: '600',
                fontSize: '18px',
                border: 'none',
                cursor: 'pointer',
                boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
                transition: 'all 0.3s ease'
              }}
              onMouseEnter={(e) => {
                e.target.style.backgroundColor = '#059669';
                e.target.style.transform = 'scale(1.05)';
              }}
              onMouseLeave={(e) => {
                e.target.style.backgroundColor = '#10b981';
                e.target.style.transform = 'scale(1)';
              }}
            >
              View Disposal Instructions
            </button>
            
            <button
              onClick={() => {
                setCurrentView('welcome');
                setUploadedImage(null);
                setClassificationResult('');
              }}
              style={{
                width: '100%',
                backgroundColor: 'white',
                color: '#374151',
                padding: '16px 32px',
                borderRadius: '16px',
                fontWeight: '600',
                fontSize: '18px',
                border: '1px solid #e5e7eb',
                cursor: 'pointer',
                boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
                transition: 'all 0.3s ease'
              }}
              onMouseEnter={(e) => {
                e.target.style.backgroundColor = '#f9fafb';
              }}
              onMouseLeave={(e) => {
                e.target.style.backgroundColor = 'white';
              }}
            >
              Scan Another Item
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (currentView === 'instructions') {
    return (
      <div style={{ minHeight: '100vh', background: 'linear-gradient(to bottom right, #ecfdf5, #dcfce7)', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '24px' }}>
        <div style={{ backgroundColor: 'white', borderRadius: '24px', maxWidth: '400px', width: '100%', boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)', overflow: 'hidden' }}>
          <div style={{ padding: '32px' }}>
            <h2 style={{ fontSize: '24px', fontWeight: 'bold', color: '#1f2937', marginBottom: '32px', textAlign: 'center' }}>
              How to Dispose of {classificationResult}
            </h2>

            <div style={{ display: 'flex', flexDirection: 'column', gap: '24px', marginBottom: '32px' }}>
              {getDisposalInstructions(classificationResult).map((instruction, index) => (
                <div key={index} style={{ display: 'flex', alignItems: 'flex-start', gap: '16px' }}>
                  <div style={{
                    width: '32px',
                    height: '32px',
                    borderRadius: '50%',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    flexShrink: 0,
                    backgroundColor: index < 3 ? '#10b981' : '#9ca3af'
                  }}>
                    <span style={{ color: 'white', fontWeight: 'bold' }}>{index + 1}</span>
                  </div>
                  <p style={{ color: '#374151', margin: 0, paddingTop: '4px', lineHeight: '1.5' }}>
                    {instruction}
                  </p>
                </div>
              ))}
            </div>

            <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
              <button
                onClick={() => setCurrentView('confirmation')}
                style={{
                  width: '100%',
                  backgroundColor: '#10b981',
                  color: 'white',
                  padding: '16px',
                  borderRadius: '16px',
                  fontWeight: '600',
                  fontSize: '18px',
                  border: 'none',
                  cursor: 'pointer',
                  transition: 'background-color 0.3s ease'
                }}
                onMouseEnter={(e) => e.target.style.backgroundColor = '#059669'}
                onMouseLeave={(e) => e.target.style.backgroundColor = '#10b981'}
              >
                Confirm Disposal
              </button>
              
              <button
                onClick={() => setCurrentView('classification')}
                style={{
                  width: '100%',
                  backgroundColor: '#f3f4f6',
                  color: '#374151',
                  padding: '12px',
                  borderRadius: '16px',
                  fontWeight: '500',
                  border: 'none',
                  cursor: 'pointer',
                  transition: 'background-color 0.3s ease'
                }}
                onMouseEnter={(e) => e.target.style.backgroundColor = '#e5e7eb'}
                onMouseLeave={(e) => e.target.style.backgroundColor = '#f3f4f6'}
              >
                Back to Results
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (currentView === 'confirmation') {
    return (
      <div style={{ minHeight: '100vh', background: 'linear-gradient(to bottom right, #ecfdf5, #dcfce7)', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '24px' }}>
        <div style={{ textAlign: 'center', maxWidth: '500px' }}>
          <div style={{ marginBottom: '32px' }}>
            <div style={{ width: '128px', height: '128px', backgroundColor: 'white', borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center', boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)', margin: '0 auto' }}>
              <div style={{ width: '64px', height: '64px', backgroundColor: '#10b981', borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                <CheckIcon size={32} />
              </div>
            </div>
          </div>

          <h2 style={{ fontSize: '36px', fontWeight: 'bold', color: '#1f2937', marginBottom: '24px' }}>Well Done!</h2>
          <p style={{ color: '#6b7280', fontSize: '18px', marginBottom: '48px', lineHeight: '1.6' }}>
            Thank you for properly disposing of your {classificationResult.toLowerCase()}! 
            You've helped make our planet cleaner and greener.
          </p>

          <button
            onClick={() => {
              setCurrentView('welcome');
              setUploadedImage(null);
              setClassificationResult('');
            }}
            style={{
              backgroundColor: '#10b981',
              color: 'white',
              padding: '16px 64px',
              borderRadius: '16px',
              fontWeight: '600',
              fontSize: '18px',
              border: 'none',
              cursor: 'pointer',
              boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
              transition: 'all 0.3s ease'
            }}
            onMouseEnter={(e) => {
              e.target.style.backgroundColor = '#059669';
              e.target.style.transform = 'scale(1.05)';
            }}
            onMouseLeave={(e) => {
              e.target.style.backgroundColor = '#10b981';
              e.target.style.transform = 'scale(1)';
            }}
          >
            Scan Another Item
          </button>
        </div>
      </div>
    );
  }

  return null;
};

export default Home;
