import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
const Banner = () => {
    const navigate = useNavigate();
    const handleScanClick = () => {
        navigate('/scan');
      };
      return (
        <div className="relative w-full bg-white overflow-hidden flex items-center justify-center py-24 " style={{ height: 'calc(100vh - 140px)' }}>
          {/* Floating Icons */}
          {/* Box - Top Left */}
          <img 
            src="../assets/carbox.png" 
            alt="Box"
            className="absolute w-32 h-32 object-contain animate-float"
            style={{ top: '8%', left: '6%' }}
          />
          
          {/* Champagne - Left */}
          <img 
            src="../assets/glass.png" 
            alt="Champagne"
            className="absolute w-32 h-32 object-contain animate-float-delayed"
            style={{ top: '28%', left: '18%' }}
          />
          
          {/* Tomato Can - Bottom Left */}
          <img 
            src="../assets/can.png" 
            alt="Tomato Can"
            className="absolute w-28 h-28 object-contain animate-float"
            style={{ bottom: '22%', left: '16%' }}
          />
          
          {/* Salad Bowl - Bottom Left Corner */}
          <img 
            src="../assets/food.png" 
            alt="Salad"
            className="absolute w-32 h-32 object-contain animate-float-delayed"
            style={{ bottom: '8%', left: '6%' }}
          />
          
          {/* Tissue Roll - Top Right */}
          <img 
            src="../assets/tissue.png" 
            alt="Tissue"
            className="absolute w-32 h-32 object-contain animate-float"
            style={{ top: '8%', right: '8%' }}
          />
          
          {/* Bubble Tea - Right */}
          <img 
            src="../assets/tea.png" 
            alt="Bubble Tea"
            className="absolute w-34 h-28 object-contain animate-float-delayed"
            style={{ top: '42%', right: '8%' }}
          />
          
          {/* Metal Screws - Bottom Right */}
          <img 
            src="../assets/paper.png" 
            alt="Metal Screws"
            className="absolute w-34 h-32 object-contain animate-float"
            style={{ bottom: '12%', right: '8%' }}
          />
    
          {/* Center Content */}
          <div className="relative z-10 flex flex-col items-center justify-center">
            {/* Main Heading with Robot */}
            <div className="relative flex items-center justify-center mb-8">
              {/* Robot Image - Center */}
              <img 
                src="../assets/bot.png" 
                alt="Robot"
                className="absolute w-64 h-64 object-contain z-20"
                style={{ top: '50%', left: '50%', transform: 'translate(-50%, -50%)' }}
              />
              
              {/* Bin Right Text */}
              <h1 className="text-9xl font-bold text-black tracking-tight" style={{ fontFamily: 'Arial, sans-serif' }}>
                Bins<span className="inline-block w-58"></span>ight
              </h1>
            </div>
            
            {/* Scan Now Button */}
            <button className="mt-12 px-16 py-2 bg-lime-400 text-black text-2xl font-bold rounded-full hover:bg-lime-500 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:scale-105" onClick={navigate('/scan')}>
              Scan now
            </button>
          </div>
    
          {/* CSS Animations */}
          <style jsx>{`
            @keyframes float {
              0%, 100% {
                transform: translateY(0px) rotate(0deg);
              }
              50% {
                transform: translateY(-20px) rotate(5deg);
              }
            }
    
            @keyframes float-delayed {
              0%, 100% {
                transform: translateY(0px) rotate(0deg);
              }
              50% {
                transform: translateY(-15px) rotate(-5deg);
              }
            }
    
            .animate-float {
              animation: float 3s ease-in-out infinite;
            }
    
            .animate-float-delayed {
              animation: float-delayed 3.5s ease-in-out infinite;
            }
          `}</style>
        </div>
      );
    };
    

export default Banner;
