import React, { useState } from 'react';

const Chat = () => {
  const [openFAQ, setOpenFAQ] = useState(0);

  const faqs = [
    {
      id: 0,
      question: "How does Binsight work?",
      answer: "Binsight uses advanced AI and computer vision to identify and sort waste materials automatically. Simply upload an image of your waste, and Binsight will classify it and provide proper disposal instructions.",
      icon: "ðŸ "
    },
    {
      id: 1,
      question: "What materials can Binsight identify?",
      answer: "Binsight can identify various waste categories including cardboard, glass, metal, paper, plastic, and general trash. Our AI is trained to recognize common household waste items.",
      icon: "ðŸ“¦"
    },
    {
      id: 2,
      question: "How do I use Binsight?",
      answer: "Using Binsight is simple! Just take a photo of your waste item or upload an image from your gallery. Our AI will analyze the image and provide you with the correct waste category and disposal instructions.",
      icon: "âš™ï¸"
    },
    {
      id: 3,
      question: "Troubleshooting common issues",
      answer: "If you're having trouble with image recognition, ensure your photo is clear and well-lit. Avoid blurry images and make sure the waste item is clearly visible in the frame.",
      icon: "ðŸ”§"
    },
    {
      id: 4,
      question: "Contact support",
      answer: "Need help? Contact our support team through the Help & Support button in the navigation. We're here to assist you with any questions or issues you may have.",
      icon: "ðŸŽ§"
    }
  ];

  const toggleFAQ = (id) => {
    setOpenFAQ(openFAQ === id ? null : id);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      

      {/* Main Content */}
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        {/* Title Section */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-800 mb-4">
            Help & Support
          </h1>
          <p className="text-xl text-gray-600">
            Your guide to using Binsight effectively.
          </p>
        </div>

        {/* FAQ Section */}
        <div className="space-y-4">
          {faqs.map((faq) => (
            <div
              key={faq.id}
              className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden"
            >
              <button
                onClick={() => toggleFAQ(faq.id)}
                className="w-full px-6 py-4 flex items-center justify-between hover:bg-gray-50 transition-colors"
              >
                <div className="flex items-center space-x-4">
                  <div className="w-10 h-10 bg-lime-100 rounded-full flex items-center justify-center">
                    <span className="text-lime-600 text-lg">{faq.icon}</span>
                  </div>
                  <h3 className="text-lg font-semibold text-gray-800 text-left">
                    {faq.question}
                  </h3>
                </div>
                <div className="flex-shrink-0">
                  <svg
                    className={`w-5 h-5 text-gray-500 transition-transform duration-200 ${
                      openFAQ === faq.id ? 'rotate-180' : ''
                    }`}
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M19 9l-7 7-7-7"
                    />
                  </svg>
                </div>
              </button>
              
              {openFAQ === faq.id && (
                <div className="px-6 pb-4">
                  <div className="pl-14">
                    <p className="text-gray-600 leading-relaxed">
                      {faq.answer}
                    </p>
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Additional Help Section */}
        <div className="mt-12 bg-lime-50 rounded-xl p-8 text-center">
          <h3 className="text-2xl font-bold text-gray-800 mb-4">
            Still need help?
          </h3>
          <p className="text-gray-600 mb-6">
            Can't find what you're looking for? Our support team is here to help.
          </p>
          <button className="bg-lime-400 text-black px-8 py-3 rounded-xl font-semibold hover:bg-lime-500 transition-colors duration-200 shadow-lg">
            Contact Support
          </button>
        </div>
      </div>
    </div>
  );
};

export default Chat;
