import React, { useState } from 'react';

const LoginRegister = () => {
  const [mode, setMode] = useState('login'); // 'login', 'register', 'forgot'
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [resetEmail, setResetEmail] = useState('');
  const [resetSent, setResetSent] = useState(false);

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (mode === 'login') {
      console.log('Login:', { username: formData.username, password: formData.password });
    } else if (mode === 'register') {
      console.log('Register:', formData);
    }
  };

  const handleForgotPassword = () => {
    if (resetEmail) {
      console.log('Password reset requested for:', resetEmail);
      setResetSent(true);
      setTimeout(() => {
        setResetSent(false);
        setResetEmail('');
        setMode('login');
      }, 3000);
    }
  };

  const switchMode = (newMode) => {
    setMode(newMode);
    setFormData({
      username: '',
      email: '',
      password: '',
      confirmPassword: ''
    });
    setResetEmail('');
    setResetSent(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-lime-50 to-lime-100 flex items-center justify-center p-4">
      {/* Header */}
      <div className="absolute top-8 left-1/2 transform -translate-x-1/2">
        <div className="flex items-center gap-3">
          <div className="flex gap-1">
            <div className="w-2 h-8 bg-emerald-500 rounded-full"></div>
            <div className="w-2 h-8 bg-emerald-500 rounded-full"></div>
            <div className="w-2 h-8 bg-emerald-500 rounded-full"></div>
          </div>
          <h1 className="text-2xl font-bold text-black">BinSight Admin</h1>
        </div>
      </div>

      {/* Login/Register Card */}
      <div className="w-full max-w-md bg-white rounded-3xl shadow-2xl p-8 relative overflow-hidden">
        {/* Back Button for Forgot Password */}
        {mode === 'forgot' && (
          <button
            onClick={() => switchMode('login')}
            className="text-emerald-600 hover:text-emerald-700 mb-6 font-medium"
          >
            ‚Üê Back to Login
          </button>
        )}

        {/* Form Content */}
        <div className="transition-all duration-300">
          {/* Forgot Password Form */}
          {mode === 'forgot' ? (
            <div>
              <h2 className="text-4xl font-bold text-black text-center mb-2">
                Forgot Password?
              </h2>
              <p className="text-gray-500 text-center mb-8">
                Enter your email address and we'll send you a link to reset your password.
              </p>

              {resetSent ? (
                <div className="bg-emerald-50 border border-emerald-200 text-emerald-700 px-4 py-3 rounded-lg text-center mb-6">
                  <p className="font-medium">Reset link sent!</p>
                  <p className="text-sm mt-1">Check your email for password reset instructions.</p>
                </div>
              ) : (
                <div className="space-y-5">
                  <div>
                    <input
                      type="email"
                      placeholder="Email Address"
                      value={resetEmail}
                      onChange={(e) => setResetEmail(e.target.value)}
                      className="w-full px-4 py-3 bg-gray-50 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-emerald-500 focus:border-transparent transition-all"
                    />
                  </div>

                  <button
                    onClick={handleForgotPassword}
                    className="w-full bg-emerald-500 hover:bg-emerald-600 text-white font-semibold py-3 rounded-full transition-all transform hover:scale-105 shadow-lg"
                  >
                    Send Reset Link
                  </button>
                </div>
              )}
            </div>
          ) : (
            /* Login/Register Forms */
            <div>
              <h2 className="text-4xl font-bold text-black text-center mb-2">
                {mode === 'login' ? 'Welcome Back' : 'Create Account'}
              </h2>
              <p className="text-gray-500 text-center mb-8">
                {mode === 'login'
                  ? 'Please enter your credentials to log in.'
                  : 'Please fill in the details to register.'}
              </p>

              <div className="space-y-5">
                {/* Username */}
                <div>
                  <input
                    type="text"
                    name="username"
                    placeholder="Username"
                    value={formData.username}
                    onChange={handleInputChange}
                    className="w-full px-4 py-3 bg-gray-50 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-emerald-500 focus:border-transparent transition-all"
                  />
                </div>

                {/* Email (Register only) */}
                {mode === 'register' && (
                  <div>
                    <input
                      type="email"
                      name="email"
                      placeholder="Email"
                      value={formData.email}
                      onChange={handleInputChange}
                      className="w-full px-4 py-3 bg-gray-50 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-emerald-500 focus:border-transparent transition-all"
                    />
                  </div>
                )}

                {/* Password */}
                <div>
                  <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={formData.password}
                    onChange={handleInputChange}
                    className="w-full px-4 py-3 bg-gray-50 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-emerald-500 focus:border-transparent transition-all"
                  />
                </div>

                {/* Confirm Password (Register only) */}
                {mode === 'register' && (
                  <div>
                    <input
                      type="password"
                      name="confirmPassword"
                      placeholder="Confirm Password"
                      value={formData.confirmPassword}
                      onChange={handleInputChange}
                      className="w-full px-4 py-3 bg-gray-50 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-emerald-500 focus:border-transparent transition-all"
                    />
                  </div>
                )}

                {/* Forgot Password (Login only) */}
                {mode === 'login' && (
                  <div className="text-left">
                    <button
                      onClick={() => switchMode('forgot')}
                      className="text-emerald-600 hover:text-emerald-700 text-sm font-medium"
                    >
                      Forgot Password?
                    </button>
                  </div>
                )}

                {/* Submit Button */}
                <button
                  onClick={handleSubmit}
                  className="w-full bg-emerald-500 hover:bg-emerald-600 text-white font-semibold py-3 rounded-full transition-all transform hover:scale-105 shadow-lg"
                >
                  {mode === 'login' ? 'Login' : 'Register'}
                </button>
              </div>

              {/* Toggle Text */}
              <p className="text-center text-gray-600 text-sm mt-6">
                {mode === 'login' ? (
                  <>
                    Don't have an account?{' '}
                    <button
                      onClick={() => switchMode('register')}
                      className="text-emerald-600 hover:text-emerald-700 font-semibold underline"
                    >
                      Register here
                    </button>
                  </>
                ) : (
                  <>
                    Already have an account?{' '}
                    <button
                      onClick={() => switchMode('login')}
                      className="text-emerald-600 hover:text-emerald-700 font-semibold underline"
                    >
                      Login here
                    </button>
                  </>
                )}
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default LoginRegister;
