import React from 'react';

const Banner = () => {
  return (
    <div className="bg-white py-16 px-4 w-full">
      <div className="w-full max-w-7xl mx-auto">
        {/* Main Banner Content */}
        <div className="relative flex items-center justify-center w-full h-96">
          
          {/* Left Text - "Bin" */}
          <div className="absolute left-4 lg:left-12 xl:left-20">
            <h1 className="text-7xl lg:text-9xl xl:text-[12rem] font-bold text-black" style={{fontFamily: 'cursive'}}>
              Bin
            </h1>
          </div>

          {/* Center Robot Placeholder */}
          <div className="flex flex-col items-center z-10">
            {/* Robot Placeholder - Replace with actual 3D robot */}
            <div className="w-40 h-48 lg:w-48 lg:h-56 bg-lime-400 rounded-3xl flex items-center justify-center shadow-2xl mb-6">
              <div className="text-black font-bold text-sm lg:text-base text-center">
                3D Robot<br/>Placeholder
              </div>
            </div>
          </div>

          {/* Right Text - "sight" */}
          <div className="absolute right-4 lg:right-12 xl:right-20">
            <h1 className="text-7xl lg:text-9xl xl:text-[12rem] font-bold text-black" style={{fontFamily: 'cursive'}}>
              sight
            </h1>
          </div>

          {/* Floating Items - Positioned around the robot */}
          {/* Top Left - Cardboard Box */}
          <div className="absolute top-8 left-16 lg:left-24 w-16 h-16 bg-amber-200 border-2 border-amber-300 rounded-lg flex items-center justify-center transform rotate-12">
            <span className="text-xs text-amber-700 font-medium">Box</span>
          </div>

          {/* Mid-Left - Champagne Bottle */}
          <div className="absolute top-20 left-8 lg:left-16 w-14 h-18 bg-green-200 border-2 border-green-300 rounded-lg flex items-center justify-center transform -rotate-6">
            <span className="text-xs text-green-700 font-medium">Bottle</span>
          </div>

          {/* Bottom Left - Salad Bowl */}
          <div className="absolute top-32 left-20 lg:left-28 w-16 h-16 bg-white border-2 border-gray-300 rounded-full flex items-center justify-center transform rotate-45">
            <span className="text-xs text-gray-600 font-medium">Bowl</span>
          </div>

          {/* Mid-Bottom Left - Tin Can */}
          <div className="absolute top-40 left-12 w-12 h-14 bg-red-200 border-2 border-red-300 rounded-lg flex items-center justify-center transform rotate-24">
            <span className="text-xs text-red-700 font-medium">Can</span>
          </div>

          {/* Top Right - Toilet Paper */}
          <div className="absolute top-8 right-16 lg:right-24 w-14 h-18 bg-white border-2 border-gray-300 rounded-lg flex items-center justify-center transform -rotate-12">
            <span className="text-xs text-gray-600 font-medium">Paper</span>
          </div>

          {/* Mid-Right - Bubble Tea */}
          <div className="absolute top-20 right-8 lg:right-16 w-14 h-18 bg-amber-200 border-2 border-amber-300 rounded-lg flex items-center justify-center transform rotate-6">
            <span className="text-xs text-amber-700 font-medium">Cup</span>
          </div>

          {/* Bottom Right - Newspaper */}
          <div className="absolute top-32 right-20 lg:right-28 w-18 h-14 bg-gray-200 border-2 border-gray-300 rounded-lg flex items-center justify-center transform -rotate-45">
            <span className="text-xs text-gray-600 font-medium">News</span>
          </div>
        </div>

        {/* Call-to-Action Button - Centered below robot */}
        <div className="flex justify-center mt-8">
          <button className="bg-lime-400 text-black px-12 py-4 rounded-2xl font-bold text-xl hover:bg-lime-500 transition-colors duration-200 shadow-lg">
            Scan now
          </button>
        </div>
      </div>
    </div>
  );
};

export default Banner;
