import React, { useState } from "react";

const Home = () => {
  const [currentView, setCurrentView] = useState("welcome");
  const [uploadedImage, setUploadedImage] = useState(null);
  const [classificationResult, setClassificationResult] = useState("");

  // SVG Icons as components
  const CameraIcon = () => (
    <svg
      width="48"
      height="48"
      fill="none"
      stroke="currentColor"
      viewBox="0 0 24 24"
      className="text-white"
    >
      <path
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeWidth={2}
        d="M3 9a2 2 0 012-2h.93a2 2 0 001.664-.89l.812-1.22A2 2 0 0110.07 4h3.86a2 2 0 011.664.89l.812 1.22A2 2 0 0018.07 7H19a2 2 0 012 2v9a2 2 0 01-2 2H5a2 2 0 01-2-2V9z"
      />
      <path
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeWidth={2}
        d="M15 13a3 3 0 11-6 0 3 3 0 016 0z"
      />
    </svg>
  );

  const UploadIcon = () => (
    <svg
      width="32"
      height="32"
      fill="none"
      stroke="currentColor"
      viewBox="0 0 24 24"
      className="text-emerald-600"
    >
      <path
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeWidth={2}
        d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12"
      />
    </svg>
  );

  const CheckIcon = ({ size = 24 }) => (
    <svg
      width={size}
      height={size}
      fill="none"
      stroke="currentColor"
      viewBox="0 0 24 24"
      className="text-white"
    >
      <path
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeWidth={3}
        d="M5 13l4 4L19 7"
      />
    </svg>
  );

  // Mock classification function
const classifyImage = async (file) => {
  if (!file) return;

  // Show processing screen immediately
  setCurrentView('processing');

  try {
    // Create FormData to send the image
    const formData = new FormData();
    formData.append('image', file);

    console.log('Sending file to API:', file);

    // Make API request
    const response = await fetch('http://localhost:5000/classify', {
      method: 'POST',
      body: formData,
    });

    console.log('Server responded with status:', response.status);

    if (!response.ok) {
      throw new Error(`Server error: ${response.status}`);
    }

    const data = await response.json();
    console.log('API classification result:', data);

    if (!data.classification) {
      throw new Error('Invalid response from server');
    }

    // Update state with classification result
    setClassificationResult(data.classification);
    setCurrentView('classification');
  } catch (error) {
    console.error('Error classifying image:', error);
    alert('Failed to classify image. Please check your server or file format.');

    // Reset to welcome screen in case of failure
    setUploadedImage(null);
    setClassificationResult('');
    setCurrentView('welcome');
  }
};


  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setUploadedImage(e.target.result);
        setCurrentView("processing");
        classifyImage(file);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleCameraCapture = () => {
    const input = document.createElement("input");
    input.type = "file";
    input.accept = "image/*";
    input.capture = "camera";
    input.onchange = handleImageUpload;
    input.click();
  };

  const getDisposalInstructions = (wasteType) => {
    const instructions = {
      Plastic: [
        "Rinse the plastic container to remove any food residue.",
        "Check for the recycling symbol (1, 2, or 5). If present, it’s recyclable.",
        "Place clean, recyclable plastics in the recycling bin.",
        "If not recyclable, dispose in general waste.",
      ],
      Glass: [
        "Remove lids or caps from glass containers.",
        "Rinse the glass to remove residue.",
        "Place clean glass in the recycling bin.",
        "Wrap broken glass safely before disposal.",
      ],
      Paper: [
        "Remove plastic wrapping or tape.",
        "Ensure paper is clean and dry.",
        "Place in the paper recycling bin.",
        "Heavily soiled paper goes to general waste.",
      ],
      Metal: [
        "Rinse cans to remove residue.",
        "Remove paper labels if possible.",
        "Place in the metal recycling bin.",
        "Aluminum and steel are recyclable.",
      ],
      Organic: [
        "Place food scraps in the organic waste bin.",
        "Remove packaging materials first.",
        "Compost at home if possible.",
        "Avoid meat or dairy in home compost.",
      ],
    };
    return instructions[wasteType] || instructions["Plastic"];
  };

  /** WELCOME SCREEN */
  if (currentView === "welcome") {
    return (
      <div className="min-h-screen flex flex-col bg-gradient-to-br from-emerald-50 to-emerald-100">
        {/* Header */}
        <div className="flex items-center px-6 py-6">
          <h1 className="text-2xl font-bold text-gray-800">BinSight</h1>
        </div>

        {/* Main */}
        <div className="flex flex-1 flex-col items-center justify-center px-6 text-center">
          <h2 className="text-4xl font-bold text-gray-800 mb-6">Time to Recycle!</h2>
          <p className="text-lg text-gray-500 mb-12 max-w-md leading-relaxed">
            Help us identify your waste. Simply tap the button below to snap a
            photo or upload one from your gallery.
          </p>

          {/* Options */}
          <div className="flex flex-col items-center gap-6">
            {/* Camera */}
            <button
              onClick={handleCameraCapture}
              className="bg-white rounded-full p-12 shadow-2xl hover:scale-105 hover:shadow-3xl transition"
            >
              <div className="flex flex-col items-center gap-3">
                <div className="p-6 bg-emerald-500 rounded-xl">
                  <CameraIcon />
                </div>
                <span className="text-xl font-semibold text-gray-800">
                  Capture Photo
                </span>
              </div>
            </button>

            {/* Divider */}
            <div className="flex items-center gap-4 w-52">
              <div className="flex-1 h-px bg-gray-300" />
              <span className="text-gray-500 font-medium">or</span>
              <div className="flex-1 h-px bg-gray-300" />
            </div>

            {/* Upload */}
            <div className="relative w-72">
              <input
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
                className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
              />
              <button className="bg-white border-2 border-emerald-200 rounded-xl p-8 w-full shadow-lg">
                <div className="flex flex-col items-center gap-3">
                  <div className="p-4 bg-emerald-50 rounded-lg">
                    <UploadIcon />
                  </div>
                  <span className="text-lg font-semibold text-gray-800">
                    Upload from Gallery
                  </span>
                </div>
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // You can continue similarly for `processing`, `classification`, `instructions`, `confirmation` screens.
  // Each inline style => Tailwind classes.

  return null;
};

export default Home;
