import React, { useState } from "react";

const EcoBinApp = () => {
  const [currentView, setCurrentView] = useState("welcome");
  const [uploadedImage, setUploadedImage] = useState(null);
  const [classificationResult, setClassificationResult] = useState("");

  // SVG Icons
  const CameraIcon = () => (
    <svg
      width="48"
      height="48"
      fill="none"
      stroke="currentColor"
      viewBox="0 0 24 24"
      className="text-white"
    >
      <path
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeWidth={2}
        d="M3 9a2 2 0 012-2h.93a2 2 0 001.664-.89l.812-1.22A2 2 0 0110.07 4h3.86a2 2 0 011.664.89l.812 1.22A2 2 0 0018.07 7H19a2 2 0 012 2v9a2 2 0 01-2 2H5a2 2 0 01-2-2V9z"
      />
      <path
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeWidth={2}
        d="M15 13a3 3 0 11-6 0 3 3 0 016 0z"
      />
    </svg>
  );

  const UploadIcon = () => (
    <svg
      width="32"
      height="32"
      fill="none"
      stroke="currentColor"
      viewBox="0 0 24 24"
      className="text-emerald-600"
    >
      <path
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeWidth={2}
        d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12"
      />
    </svg>
  );

  const CheckIcon = ({ size = 24 }) => (
    <svg
      width={size}
      height={size}
      fill="none"
      stroke="currentColor"
      viewBox="0 0 24 24"
      className="text-white"
    >
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M5 13l4 4L19 7" />
    </svg>
  );

  const classifyImage = (file) => {
    setTimeout(() => {
      const wasteTypes = ["Plastic", "Glass", "Paper", "Metal", "Organic"];
      const randomType = wasteTypes[Math.floor(Math.random() * wasteTypes.length)];
      setClassificationResult(randomType);
      setCurrentView("classification");
    }, 1500);
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setUploadedImage(e.target.result);
        setCurrentView("processing");
        classifyImage(file);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleCameraCapture = () => {
    const input = document.createElement("input");
    input.type = "file";
    input.accept = "image/*";
    input.capture = "camera";
    input.onchange = handleImageUpload;
    input.click();
  };

  const getDisposalInstructions = (wasteType) => {
    const instructions = {
      Plastic: [
        "Rinse the plastic container to remove any food residue.",
        "Check for the recycling symbol (usually numbers 1, 2, or 5). If present, it's recyclable.",
        "Place clean, recyclable plastics in the recycling bin.",
        "If not recyclable, dispose in general waste.",
      ],
      Glass: [
        "Remove lids or caps.",
        "Rinse glass.",
        "Place clean glass in recycling bin.",
        "Wrap broken glass safely.",
      ],
      Paper: [
        "Remove tape/plastic.",
        "Ensure paper is dry.",
        "Place in paper recycling.",
        "Heavily soiled paper → general waste.",
      ],
      Metal: [
        "Rinse cans.",
        "Remove labels.",
        "Place in metal recycling.",
        "Aluminum and steel are recyclable.",
      ],
      Organic: [
        "Place food scraps in organic bin.",
        "Remove packaging.",
        "Compost if possible.",
        "Avoid meat/dairy in compost.",
      ],
    };
    return instructions[wasteType] || instructions["Plastic"];
  };

  // --- PROCESSING SCREEN ---
  if (currentView === "processing") {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-emerald-50 to-emerald-100 p-6">
        <div className="text-center">
          {uploadedImage && (
            <img
              src={uploadedImage}
              alt="Uploaded"
              className="w-48 h-48 object-cover rounded-xl shadow-lg mb-8 mx-auto"
            />
          )}
          <div className="w-16 h-16 bg-emerald-500 rounded-full mx-auto flex items-center justify-center mb-6">
            <div
              className="w-8 h-8 border-4 border-white border-t-transparent rounded-full animate-spin"
            ></div>
          </div>
          <h2 className="text-3xl font-bold text-gray-800 mb-2">Analyzing Image...</h2>
          <p className="text-gray-500 text-lg">
            Our AI is identifying your waste type to provide the best recycling guidance.
          </p>
        </div>
      </div>
    );
  }

  // --- CLASSIFICATION SCREEN ---
  if (currentView === "classification") {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-emerald-50 to-emerald-100 p-6">
        <div className="text-center max-w-md">
          {uploadedImage && (
            <img
              src={uploadedImage}
              alt="Classified"
              className="w-32 h-32 object-cover rounded-xl shadow-md mb-6 mx-auto"
            />
          )}
          <div className="w-24 h-24 bg-white rounded-full flex items-center justify-center mx-auto mb-6 shadow-lg">
            <div className="w-12 h-12 bg-emerald-500 rounded-full flex items-center justify-center">
              <CheckIcon size={24} />
            </div>
          </div>
          <h2 className="text-4xl font-bold text-gray-800 mb-4">{classificationResult}</h2>
          <p className="text-gray-500 text-lg mb-6">
            Great! We've identified your waste as {classificationResult.toLowerCase()}. 
            Let's learn how to dispose of it properly.
          </p>
          <div className="flex flex-col gap-4">
            <button
              onClick={() => setCurrentView("instructions")}
              className="bg-emerald-500 hover:bg-emerald-600 text-white font-semibold py-3 rounded-xl transition"
            >
              View Disposal Instructions
            </button>
            <button
              onClick={() => {
                setCurrentView("welcome");
                setUploadedImage(null);
                setClassificationResult("");
              }}
              className="bg-white border border-gray-300 hover:bg-gray-100 text-gray-700 font-semibold py-3 rounded-xl transition"
            >
              Scan Another Item
            </button>
          </div>
        </div>
      </div>
    );
  }

  // --- INSTRUCTIONS SCREEN ---
  if (currentView === "instructions") {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-emerald-50 to-emerald-100 p-6">
        <div className="bg-white rounded-2xl shadow-lg max-w-md w-full p-8">
          <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">
            How to Dispose of {classificationResult}
          </h2>
          <div className="flex flex-col gap-4 mb-6">
            {getDisposalInstructions(classificationResult).map((inst, i) => (
              <div key={i} className="flex items-start gap-3">
                <div className={`w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0 ${i < 3 ? 'bg-emerald-500' : 'bg-gray-400'}`}>
                  <span className="text-white font-bold">{i + 1}</span>
                </div>
                <p className="text-gray-700">{inst}</p>
              </div>
            ))}
          </div>
          <div className="flex flex-col gap-3">
            <button
              onClick={() => setCurrentView("confirmation")}
              className="bg-emerald-500 hover:bg-emerald-600 text-white py-3 rounded-xl font-semibold transition"
            >
              Confirm Disposal
            </button>
            <button
              onClick={() => setCurrentView("classification")}
              className="bg-gray-200 hover:bg-gray-300 text-gray-700 py-3 rounded-xl font-semibold transition"
            >
              Back to Results
            </button>
          </div>
        </div>
      </div>
    );
  }

  // --- CONFIRMATION SCREEN ---
  if (currentView === "confirmation") {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-emerald-50 to-emerald-100 p-6">
        <div className="text-center max-w-md">
          <div className="mb-6">
            <div className="w-32 h-32 bg-white rounded-full mx-auto flex items-center justify-center shadow-lg">
              <div className="w-16 h-16 bg-emerald-500 rounded-full flex items-center justify-center">
                <CheckIcon size={32} />
              </div>
            </div>
          </div>
          <h2 className="text-4xl font-bold text-gray-800 mb-4">Well Done!</h2>
          <p className="text-gray-500 text-lg mb-6">
            Thank you for properly disposing of your {classificationResult.toLowerCase()}! 
            You've helped make our planet cleaner and greener.
          </p>
          <button
            onClick={() => {
              setCurrentView("welcome");
              setUploadedImage(null);
              setClassificationResult("");
            }}
            className="bg-emerald-500 hover:bg-emerald-600 text-white py-3 px-8 rounded-xl font-semibold transition"
          >
            Scan Another Item
          </button>
        </div>
      </div>
    );
  }

  return null;
};

export default EcoBinApp;
