import React, { useState } from 'react'
import { Send, Paperclip } from 'lucide-react';

function Chat() {
    const inital = [
        {
          id: 1,
          type: 'bot',
          text: 'Hi there! How can I help you today?',
          time: '10:30 AM'
        },
        {
          id: 2,
          type: 'user',
          text: 'How do I recycle plastic bottles?',
          name: 'Sophia',
          time: '10:31 AM'
        },
        {
          id: 3,
          type: 'bot',
          text: 'Plastic bottles should be rinsed and placed in the recycling bin. Make sure they are empty and free of any liquids.',
          time: '10:31 AM'
        },
        {
          id: 4,
          type: 'user',
          text: 'What about glass jars?',
          name: 'Sophia',
          time: '10:32 AM'
        },
        {
          id: 5,
          type: 'bot',
          text: 'Glass jars should also be rinsed and placed in the recycling bin. Remove any lids if possible.',
          time: '10:32 AM'
        }
      ];
    const [messages, setMessages] = useState([]);

      const [inputMessage, setInputMessage] = useState('');

      const handleSendMessage = () => {
        if (inputMessage.trim()) {
          // Add user message
          const newMessage = {
            id: messages.length + 1,
            type: 'user',
            text: inputMessage,
            name: 'User',
            time: new Date().toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' })
          };
          setMessages([...messages, newMessage]);
          setInputMessage('');

          // Simulate bot response
          setTimeout(() => {
            const botResponse = {
              id: messages.length + 2,
              type: 'bot',
              text: 'Thank you for your question! I\'m here to help with waste disposal guidance.',
              time: new Date().toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' })
            };
            setMessages(prev => [...prev, botResponse]);
          }, 1000);
        }
      };

      return (
        <div className="w-full bg-gradient-to-br from-lime-50 to-lime-100 flex flex-col px-44" style={{ height: '600px' }}>
          {/* Header Section */}
          <div className="px-8 pt-8 pb-6 bg-gradient-to-br from-lime-50 to-lime-100">
            <h1 className="text-4xl font-bold text-gray-900 mb-2">Support Assistant</h1>
            <p className="text-lg text-green-600">Ask our AI assistant any questions about waste disposal.</p>
          </div>

          {/* Chat Messages Container */}
          <div className="flex-1 overflow-y-auto px-8 py-6 space-y-6">
            {messages.map((message) => (
              <div key={message.id} className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}>
                {message.type === 'bot' && (
                  <div className="flex items-start gap-3">
                    <div className="w-10 h-10 rounded-full bg-gray-700 flex items-center justify-center flex-shrink-0">
                      <div className="w-6 h-6 bg-gray-500 rounded-full"></div>
                    </div>
                    <div className="flex flex-col">
                      <span className="text-sm text-gray-600 mb-1">EcoBin Assistant</span>
                      <div className="bg-gray-200 rounded-2xl rounded-tl-sm px-5 py-3 max-w-md">
                        <p className="text-gray-800 text-base leading-relaxed">{message.text}</p>
                      </div>
                    </div>
                  </div>
                )}

                {message.type === 'user' && (
                  <div className="flex items-start gap-3 flex-row-reverse">
                    <div className="w-10 h-10 rounded-full bg-amber-200 flex items-center justify-center flex-shrink-0 overflow-hidden">
                      <div className="w-full h-full bg-gradient-to-br from-amber-300 to-amber-400"></div>
                    </div>
                    <div className="flex flex-col items-end">
                      <span className="text-sm text-gray-600 mb-1">{message.name}</span>
                      <div className="bg-green-400 rounded-2xl rounded-tr-sm px-5 py-3 max-w-md">
                        <p className="text-white text-base leading-relaxed">{message.text}</p>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>

          {/* Input Section */}
          <div className="px-8 pb-8 pt-4 bg-gradient-to-br from-lime-50 to-lime-100">
            <div className="relative flex items-center gap-3">
              <div className="flex-1 relative">
                <input
                  type="text"
                  value={inputMessage}
                  onChange={(e) => setInputMessage(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                  placeholder="Type your message..."
                  className="w-full px-5 py-4 pr-14 bg-gray-200 rounded-full text-gray-700 placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-green-400 text-base"
                />
                <button className="absolute right-4 top-1/2 transform -translate-y-1/2 text-gray-500 hover:text-gray-700">
                  <Paperclip size={20} />
                </button>
              </div>
              <button
                onClick={handleSendMessage}
                className="w-12 h-12 bg-green-400 rounded-full flex items-center justify-center hover:bg-green-500 transition-colors shadow-lg"
              >
                <Send size={20} className="text-white" />
              </button>
            </div>
          </div>
        </div>
      );
}

export default Chat
