import React, { useState } from 'react';
import { LayoutDashboard, Trash2, FolderTree, MessageSquare, Users, LogOut } from 'lucide-react';

const AdminSidebar = () => {
  const [activeItem, setActiveItem] = useState('Categories');

  const menuItems = [
    { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
    { icon: Trash2, label: 'Bins', path: '/bins' },
    { icon: FolderTree, label: 'Categories', path: '/categories' },
    { icon: MessageSquare, label: 'Chatbot', path: '/chatbot' },
    { icon: Users, label: 'Users', path: '/users' },
  ];

  return (
    <div className="h-screen w-64 bg-gradient-to-br from-lime-50 to-lime-100 flex flex-col ml-10px">
     
      {/* Navigation Items */}
      <nav className="flex-1 px-4">
        {menuItems.map((item) => {
          const Icon = item.icon;
          const isActive = activeItem === item.label;
          
          return (
            <button
              key={item.label}
              onClick={() => setActiveItem(item.label)}
              className={`w-full flex items-center gap-3 px-4 py-3 mb-2 rounded-lg transition-all ${
                isActive
                  ? 'bg-emerald-100 text-emerald-600'
                  : 'text-gray-700 hover:bg-white/50'
              }`}
            >
              <Icon size={20} />
              <span className="font-medium">{item.label}</span>
            </button>
          );
        })}
      </nav>

      {/* Logout Button */}
      <div className="p-4 mt-auto">
        <button className="w-full flex items-center gap-3 px-4 py-3 text-gray-700 hover:bg-white/50 rounded-lg transition-all">
          <LogOut size={20} />
          <span className="font-medium">Logout</span>
        </button>
      </div>
    </div>
  );
};

export default AdminSidebar;
