import React, { useState } from 'react';
import { Edit2, Trash2, Save, Plus } from 'lucide-react';

const Chat = () => {
  const [responses, setResponses] = useState([
    {
      id: 1,
      trigger: 'How do I dispose of batteries?',
      response: 'Batteries should be taken to designated recycling centers. Never throw them in regular trash as they contain harmful chemicals.'
    },
    {
      id: 2,
      trigger: 'What about pizza boxes?',
      response: 'Clean pizza boxes can be recycled. If greasy or food-stained, compost them or dispose in regular waste.'
    }
  ]);

  const [editingId, setEditingId] = useState(null);
  const [newTrigger, setNewTrigger] = useState('');
  const [newResponse, setNewResponse] = useState('');

  const handleEdit = (id) => {
    const item = responses.find(r => r.id === id);
    setNewTrigger(item.trigger);
    setNewResponse(item.response);
    setEditingId(id);
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this response?')) {
      setResponses(responses.filter(r => r.id !== id));
    }
  };

  const handleSave = () => {
    if (!newTrigger.trim() || !newResponse.trim()) {
      alert('Please fill in both fields');
      return;
    }

    if (editingId) {
      setResponses(responses.map(r => 
        r.id === editingId 
          ? { ...r, trigger: newTrigger, response: newResponse }
          : r
      ));
      setEditingId(null);
    } else {
      const newItem = {
        id: Math.max(...responses.map(r => r.id), 0) + 1,
        trigger: newTrigger,
        response: newResponse
      };
      setResponses([...responses, newItem]);
    }

    setNewTrigger('');
    setNewResponse('');
  };

  const handleAddNew = () => {
    setNewTrigger('');
    setNewResponse('');
    setEditingId(null);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-lime-50 to-lime-100 p-8">
      <div className="max-w-2xl mx-auto">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Chatbot Responses</h1>
          <p className="text-gray-600">Manage automated responses for common waste disposal questions</p>
        </div>

        <div className="bg-white rounded-2xl shadow-lg p-8">
          <h2 className="text-xl font-bold text-gray-900 mb-6">Chatbot Responses</h2>

          <div className="space-y-3 mb-8">
            {responses.map((item) => (
              <div
                key={item.id}
                className="flex items-center justify-between bg-emerald-50 hover:bg-emerald-100 px-6 py-4 rounded-lg transition-colors group"
              >
                <span className="text-gray-700 font-medium flex-1">"{item.trigger}"</span>
                <div className="flex items-center gap-2">
                  <button
                    onClick={() => handleEdit(item.id)}
                    className="p-2 text-gray-500 hover:text-blue-600 hover:bg-white rounded-lg transition-all opacity-0 group-hover:opacity-100"
                    title="Edit"
                  >
                    <Edit2 size={18} />
                  </button>
                  <button
                    onClick={() => handleDelete(item.id)}
                    className="p-2 text-gray-500 hover:text-red-600 hover:bg-white rounded-lg transition-all opacity-0 group-hover:opacity-100"
                    title="Delete"
                  >
                    <Trash2 size={18} />
                  </button>
                </div>
              </div>
            ))}
          </div>

          <div className="space-y-6">
            <div>
              <label className="block text-sm font-semibold text-gray-900 mb-2">
                User Question (Trigger)
              </label>
              <input
                type="text"
                value={newTrigger}
                onChange={(e) => setNewTrigger(e.target.value)}
                placeholder="e.g., How do I dispose of old electronics?"
                className="w-full px-4 py-3 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-emerald-500"
              />
            </div>

            <div>
              <label className="block text-sm font-semibold text-gray-900 mb-2">
                Chatbot Response
              </label>
              <textarea
                value={newResponse}
                onChange={(e) => setNewResponse(e.target.value)}
                placeholder="Provide a helpful and clear response..."
                rows={6}
                className="w-full px-4 py-3 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-emerald-500 resize-none"
              ></textarea>
            </div>

            <div className="flex gap-4">
              <button
                onClick={handleAddNew}
                className="px-8 py-3 bg-gray-100 hover:bg-gray-200 text-gray-700 font-semibold rounded-lg transition-colors"
              >
                Add New
              </button>
              <button
                onClick={handleSave}
                className="flex items-center gap-2 px-8 py-3 bg-emerald-500 hover:bg-emerald-600 text-white font-semibold rounded-lg transition-colors"
              >
                <Save size={20} />
                Save Response
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chat;
