import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { LayoutDashboard, Trash2, FolderTree, MessageSquare, Users, LogOut, BrainCircuit, FileDown } from 'lucide-react';

const AdminSidebar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [activeItem, setActiveItem] = useState('Categories');

  const menuItems = [
    { icon: LayoutDashboard, label: 'Dashboard', path: '/admin/dashboard' },
    { icon: BrainCircuit, label: 'Model', path: '/admin/model' },
    { icon: FolderTree, label: 'Categories', path: '/admin/categories' },
    { icon: MessageSquare, label: 'Chatbot', path: '/admin/chatbot' },
    { icon: FileDown, label: 'Export', path: '/admin/export' },
  ];

  // Update active item based on current path
  useEffect(() => {
    const currentItem = menuItems.find(item => location.pathname === item.path);
    if (currentItem) {
      setActiveItem(currentItem.label);
    }
  }, [location.pathname]);

  const handleNavigation = (item) => {
    setActiveItem(item.label);
    navigate(item.path);
  };

  const handleLogout = () => {
    // Add your logout logic here
    localStorage.removeItem('userRole');
    navigate('/');
  };

  return (
    <div className="h-screen w-64 bg-gradient-to-br from-lime-50 to-lime-100 flex flex-col">
      {/* Logo Section */}
      <div className="p-6">
        <h1 className="text-2xl font-bold text-black">BinSight</h1>
      </div>

      {/* Navigation Items */}
      <nav className="flex-1 px-4">
        {menuItems.map((item) => {
          const Icon = item.icon;
          const isActive = activeItem === item.label;
          
          return (
            <button
              key={item.label}
              onClick={() => handleNavigation(item)}
              className={`w-full flex items-center gap-3 px-4 py-3 mb-2 rounded-lg transition-all ${
                isActive
                  ? 'bg-emerald-100 text-emerald-600'
                  : 'text-gray-700 hover:bg-white/50'
              }`}
            >
              <Icon size={20} />
              <span className="font-medium">{item.label}</span>
            </button>
          );
        })}
      </nav>

      {/* Logout Button */}
      <div className="p-4 mt-auto">
        <button 
          onClick={handleLogout}
          className="w-full flex items-center gap-3 px-4 py-3 text-gray-700 hover:bg-white/50 rounded-lg transition-all"
        >
          <LogOut size={20} />
          <span className="font-medium">Logout</span>
        </button>
      </div>
    </div>
  );
};

export default AdminSidebar;
