import React, { useState } from 'react';
import { Upload, CloudUpload, Play, Clock } from 'lucide-react';

const Model = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [isRetraining, setIsRetraining] = useState(false);
  const [progress, setProgress] = useState(0);

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    const files = Array.from(e.dataTransfer.files);
    handleFiles(files);
  };

  const handleFileInput = (e) => {
    const files = Array.from(e.target.files);
    handleFiles(files);
  };

  const handleFiles = (files) => {
    const validFiles = files.filter(file => {
      const validTypes = ['image/jpeg', 'image/png'];
      const maxSize = 10 * 1024 * 1024; // 10MB
      return validTypes.includes(file.type) && file.size <= maxSize;
    });
    
    setUploadedFiles([...uploadedFiles, ...validFiles]);
  };

  const handleStartRetraining = () => {
    if (uploadedFiles.length === 0) {
      alert('Please upload training images first');
      return;
    }

    setIsRetraining(true);
    setProgress(0);

    // Simulate retraining progress
    const interval = setInterval(() => {
      setProgress(prev => {
        if (prev >= 100) {
          clearInterval(interval);
          setIsRetraining(false);
          return 100;
        }
        return prev + 10;
      });
    }, 500);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-lime-50 to-lime-100 p-8">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">AI Model Management</h1>
          <p className="text-emerald-600">Manage your AI model's training and performance.</p>
        </div>

        {/* Upload Training Images Section */}
        <div className="bg-white rounded-2xl shadow-lg p-8 mb-6">
          <div className="mb-6">
            <h2 className="text-xl font-bold text-gray-900 mb-2">Upload Training Images</h2>
            <p className="text-sm text-emerald-600">Add new images to improve the model's accuracy.</p>
          </div>

          <div
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            className={`border-2 border-dashed rounded-xl p-12 transition-all ${
              isDragging
                ? 'border-emerald-500 bg-emerald-50'
                : 'border-gray-300 bg-gray-50'
            }`}
          >
            <div className="flex flex-col items-center justify-center">
              <CloudUpload className="text-gray-400 mb-4" size={64} />
              <p className="text-lg font-semibold text-gray-900 mb-2">Drag & drop files here</p>
              <p className="text-sm text-gray-500 mb-4">or</p>
              <label className="cursor-pointer">
                <input
                  type="file"
                  multiple
                  accept="image/jpeg,image/png"
                  onChange={handleFileInput}
                  className="hidden"
                />
                <div className="flex items-center gap-2 bg-emerald-500 hover:bg-emerald-600 text-white font-semibold px-6 py-3 rounded-lg transition-colors">
                  <Upload size={20} />
                  Browse Files
                </div>
              </label>
              <p className="text-xs text-gray-500 mt-4">Supported formats: JPG, PNG | Max file size: 10MB</p>
            </div>
          </div>

          {uploadedFiles.length > 0 && (
            <div className="mt-4">
              <p className="text-sm text-gray-700 font-medium mb-2">
                Uploaded {uploadedFiles.length} file(s)
              </p>
              <div className="flex flex-wrap gap-2">
                {uploadedFiles.map((file, index) => (
                  <span
                    key={index}
                    className="px-3 py-1 bg-emerald-100 text-emerald-700 text-xs font-medium rounded-full"
                  >
                    {file.name}
                  </span>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Retrain Model Section */}
        <div className="bg-white rounded-2xl shadow-lg p-8 mb-6">
          <div className="mb-6">
            <h2 className="text-xl font-bold text-gray-900 mb-2">Retrain Model</h2>
            <p className="text-sm text-emerald-600">Initiate the retraining process with the newly uploaded images.</p>
          </div>

          <div className="flex items-center justify-between bg-emerald-50 p-6 rounded-xl">
            <div className="flex items-center gap-4">
              <div className="bg-emerald-100 p-3 rounded-lg">
                <CloudUpload className="text-emerald-600" size={24} />
              </div>
              <div>
                <h3 className="text-base font-bold text-gray-900 mb-1">Trigger Model Retraining</h3>
                <p className="text-sm text-gray-600">This process may take a significant amount of time.</p>
              </div>
            </div>
            <button
              onClick={handleStartRetraining}
              disabled={isRetraining}
              className={`flex items-center gap-2 px-6 py-3 rounded-lg font-semibold transition-colors ${
                isRetraining
                  ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  : 'bg-emerald-500 hover:bg-emerald-600 text-white'
              }`}
            >
              <Play size={20} />
              {isRetraining ? 'Retraining...' : 'Start Retraining'}
            </button>
          </div>
        </div>

        {/* Retraining Progress Section */}
        <div className="bg-white rounded-2xl shadow-lg p-8">
          <h2 className="text-xl font-bold text-gray-900 mb-6">Retraining Progress</h2>

          <div className="space-y-3">
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm font-semibold text-gray-900">Model Retraining</span>
              <span className="text-sm font-bold text-emerald-600">{progress}%</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-3 overflow-hidden">
              <div
                className="bg-emerald-500 h-3 rounded-full transition-all duration-500"
                style={{ width: `${progress}%` }}
              ></div>
            </div>
            <div className="flex items-center gap-2 text-sm text-gray-600 mt-2">
              <Clock size={16} />
              <span>
                {isRetraining
                  ? `Estimated time remaining: ${Math.ceil((100 - progress) / 10)} minutes`
                  : progress === 100
                  ? 'Retraining completed!'
                  : 'Estimated time remaining: 2 hours'}
              </span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Model;
